from scipy.stats import norm
#let b_in stand for bare bias
def b_eff(sigma: float, b_in:float) -> float:
    alpha = sigma*abs(b_in)
    return b_in*norm.cdf(1/alpha)/(norm.cdf(1/alpha)+alpha*norm.pdf(1/alpha))
